$.fn.addTriggersToJqueryValidate = function () {
    return this.each(function () {
        var form = $(this); var validator = form.data("validator"); if (!validator) return; if (form.data("jQueryValidateTriggersAdded")) return; else form.data("jQueryValidateTriggersAdded", true); var oldForm = validator.form; validator.form = function () { var result = oldForm.apply(this, arguments); var form = this.currentForm; $(form).trigger(result == true ? "formValidationSuccess" : "formValidationError", form); $(form).trigger("formValidation", [form, result]); return result };
        var oldElement = validator.element; validator.element = function (element) { var result = oldElement.apply(this, arguments); $(element).trigger(result == true ? "elementValidationSuccess" : "elementValidationError", element); $(element).trigger("elementValidation", [element, result]); return result }
    })
};